mapper.select:
select 

from pc_product_keywords
where product = #{product,jdbcType=VARCHAR}
  and keywords = #{keywords,jdbcType=VARCHAR}

mapper.delete:
delete from pc_product_keywords
where product = #{product,jdbcType=VARCHAR}
  and keywords = #{keywords,jdbcType=VARCHAR}

mapper.insert:
insert into pc_product_keywords (product, keywords, uid, 
  sort, create_user, create_time
  )
values (#{product,jdbcType=VARCHAR}, #{keywords,jdbcType=VARCHAR}, #{uid,jdbcType=VARCHAR}, 
  #{sort,jdbcType=BIGINT}, #{createUser,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update pc_product_keywords
set uid = #{uid,jdbcType=VARCHAR},
  sort = #{sort,jdbcType=BIGINT},
  create_user = #{createUser,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where product = #{product,jdbcType=VARCHAR}
  and keywords = #{keywords,jdbcType=VARCHAR}
