mapper.select:
select

from pc_product_spec
where product = #{product,jdbcType=VARCHAR}
and
spec_key = #{specKey,jdbcType=VARCHAR}

mapper.delete:
delete from
pc_product_spec
where product = #{product,jdbcType=VARCHAR}
and spec_key
=
#{specKey,jdbcType=VARCHAR}

mapper.update:
update pc_product_spec

	
		spec_value = #{specValue,jdbcType=VARCHAR}
	
	
		type = #{type,jdbcType=INTEGER},
	
	
		sort = #{sort,jdbcType=BIGINT},
	
	update_user = #{updateUser,jdbcType=VARCHAR},
	update_time =
	#{updateTime,jdbcType=TIMESTAMP},

where product = #{product,jdbcType=VARCHAR}
and spec_key =
#{specKey,jdbcType=VARCHAR}
